#include "gtest/gtest.h"
#include <vector>
#include <string>

#include "30_SubstringwithConcatenationofAllWords.cpp"

struct ParamsAndExpectedResult
{
	std::string s;
	std::vector<std::string> substrings;
	std::vector<int> result;
};

template<typename T>
bool EqualVector(const std::vector<T>& a, const std::vector<T>& b)
{
	if (a.size() != b.size())
		return false;


	for (int i = 0; i < a.size(); ++i)
	{
		if (a[i] != b[i])
			return false;
	}
	return true;
}

TEST(SubstringwithConcatenationofAllWords, NormalTest)
{
	std::vector<ParamsAndExpectedResult> test_cases = 
	{
		{"", {"a", "b"}, {}},
		{"aaa", {"a", "a"}, {0,1}},
		{"barfoothefoobarman", {"foo", "bar"}, {0,9} },
	};

	for (int i = 0; i < test_cases.size(); ++i)
	{
		std::vector<int> actual = Solution().findSubstring(test_cases[i].s, test_cases[i].substrings);
		EXPECT_TRUE(EqualVector(actual, test_cases[i].result))
			<< "Index: " << i;
	}
}

